import React, { useRef, useState, useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom";

function MusicPlay() {
  const { state } = useLocation();
  const navigate = useNavigate();
  const audioRef = useRef(null);

  const playlist = state?.songs || [];
  const [currentIndex, setCurrentIndex] = useState(state?.index || 0);
  const song = playlist[currentIndex];

  const [isPlaying, setIsPlaying] = useState(false);
  const [progress, setProgress] = useState(0);
  const [volume, setVolume] = useState(0.8);

  useEffect(() => {
    if (audioRef.current && song) {
      audioRef.current.load();
      audioRef.current.volume = volume;
      audioRef.current
        .play()
        .then(() => setIsPlaying(true))
        .catch(() => setIsPlaying(false));
    }
  }, [currentIndex]);

  if (!song) {
    return (
      <div className="container text-center mt-5">
        <h4>No song selected</h4>
        <button className="btn btn-primary" onClick={() => navigate("/")}>
          Go Back
        </button>
      </div>
    );
  }

  const togglePlay = () => {
    if (isPlaying) {
      audioRef.current.pause();
      setIsPlaying(false);
    } else {
      audioRef.current.play();
      setIsPlaying(true);
    }
  };

  const nextSong = () => {
    if (currentIndex < playlist.length - 1) {
      setCurrentIndex(currentIndex + 1);
    }
  };

  const prevSong = () => {
    if (currentIndex > 0) {
      setCurrentIndex(currentIndex - 1);
    }
  };

  const updateProgress = () => {
    const current = audioRef.current.currentTime;
    const duration = audioRef.current.duration || 1;
    setProgress((current / duration) * 100);
  };

  const seekSong = (e) => {
    audioRef.current.currentTime =
      (e.target.value / 100) * audioRef.current.duration;
  };

  return (
    <div
      style={{
        background: "linear-gradient(135deg, #000000, #0a2540)",
        height: "100vh",
        color: "white",
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "center"
      }}
    >
      <h3>{song.name}</h3>
      <p className="text-secondary">{song.artist_name}</p>

      <input
        type="range"
        min="0"
        max="100"
        value={progress}
        onChange={seekSong}
        style={{ width: "60%" }}
      />

      <div className="mt-4">
        <button className="btn btn-light mx-2" onClick={prevSong}>
          ⏮
        </button>

        <button className="btn btn-success mx-2" onClick={togglePlay}>
          {isPlaying ? "⏸ Pause" : "▶ Play"}
        </button>

        <button className="btn btn-light mx-2" onClick={nextSong}>
          ⏭
        </button>
      </div>

      <input
        type="range"
        min="0"
        max="1"
        step="0.01"
        value={volume}
        onChange={(e) => setVolume(e.target.value)}
        className="mt-3"
        style={{ width: "150px" }}
      />

      <audio
        ref={audioRef}
        src={song.audio_url}   // ✅ FIXED
        onTimeUpdate={updateProgress}
        onEnded={nextSong}
      />
    </div>
  );
}

export default MusicPlay;
