import React, { useState, useContext } from 'react';
import { PlayerContext } from '../context/PlayerContext';

function MusicSearch() {
  const [query, setQuery] = useState('');
  const [language, setLanguage] = useState('en');
  const { songs, setSongs, currentIndex, playSong, playNext, playPrev } = useContext(PlayerContext);

  const handleSearch = async () => {
    if (!query) return;
    try {
      const res = await fetch(`http://127.0.0.1:5000/api/music/search?query=${query}&lang=${language}`);
      const data = await res.json();
      setSongs(data.results);
    } catch (err) {
      console.error(err);
      alert("Error fetching songs");
    }
  };

  return (
    <div>
      {/* Language selector */}
      <select className="form-select mb-3" value={language} onChange={(e) => setLanguage(e.target.value)}>
        <option value="en">English</option>
        <option value="si">Sinhala</option>
      </select>

      {/* Search Box */}
      <div className="input-group mb-4">
        <input
          type="text"
          className="form-control"
          placeholder={`Search ${language === 'en' ? 'English' : 'Sinhala'} songs...`}
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          onKeyDown={(e) => e.key === 'Enter' && handleSearch()}
        />
        <button className="btn btn-primary" onClick={handleSearch}>Search</button>
      </div>

      {/* Songs List */}
      <div className="row">
        {songs.map((song, index) => (
          <div key={song.id} className="col-6 col-md-4 mb-3">
            <div
              className={`card ${currentIndex === index ? 'border-success' : ''}`}
              style={{ cursor: 'pointer' }}
              onClick={() => playSong(index)}
            >
              <img src={song.album_image || 'https://via.placeholder.com/150'} className="card-img-top" alt={song.name}/>
              <div className="card-body p-2">
                <h6 className="card-title mb-0">{song.name}</h6>
                <small className="text-muted">{song.artist_name} - {song.album_name}</small>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default MusicSearch;
