import React, { useRef, useState, useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom";

function AudioPlayerPage() {
  const { state } = useLocation();
  const navigate = useNavigate();
  const audioRef = useRef(null);

  const playlist = state?.songs || [];
  const [index, setIndex] = useState(state?.index || 0);
  const song = playlist[index];

  const [isPlaying, setIsPlaying] = useState(true);
  const [progress, setProgress] = useState(0);
  const [volume, setVolume] = useState(0.8);

  useEffect(() => {
    if (audioRef.current && song) {
      audioRef.current.play();
      setIsPlaying(true);
    }
  }, [index, song]);

  if (!song) {
    return (
      <div className="container text-center mt-5">
        <h3>No song selected</h3>
        <button className="btn btn-primary" onClick={() => navigate("/")}>
          Go Back
        </button>
      </div>
    );
  }

  const togglePlay = () => {
    if (isPlaying) audioRef.current.pause();
    else audioRef.current.play();
    setIsPlaying(!isPlaying);
  };

  const nextSong = () => {
    if (index < playlist.length - 1) setIndex(index + 1);
  };

  const prevSong = () => {
    if (index > 0) setIndex(index - 1);
  };

  const updateProgress = () => {
    const current = audioRef.current.currentTime;
    const duration = audioRef.current.duration;
    setProgress((current / duration) * 100);
  };

  const seek = (e) => {
    const time = (e.target.value / 100) * audioRef.current.duration;
    audioRef.current.currentTime = time;
  };

  return (
    <div
      style={{
        background: "linear-gradient(135deg, #000000, #0a2540)",
        height: "100vh",
        color: "white",
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "center"
      }}
    >
      {/* Song Info */}
      <h4>{song.name}</h4>
      <p className="text-secondary">{song.artist_name}</p>

      {/* Progress */}
      <input
        type="range"
        min="0"
        max="100"
        value={progress}
        onChange={seek}
        style={{ width: "60%" }}
      />

      {/* Controls */}
      <div className="mt-3">
        <button className="btn btn-light mx-2" onClick={prevSong}>⏮</button>
        <button className="btn btn-success mx-2" onClick={togglePlay}>
          {isPlaying ? "⏸ Pause" : "▶ Play"}
        </button>
        <button className="btn btn-light mx-2" onClick={nextSong}>⏭</button>
      </div>

      {/* Volume */}
      <input
        type="range"
        min="0"
        max="1"
        step="0.01"
        value={volume}
        onChange={(e) => {
          setVolume(e.target.value);
          audioRef.current.volume = e.target.value;
        }}
        className="mt-3"
        style={{ width: "150px" }}
      />

      {/* Audio */}
      <audio
        ref={audioRef}
        src={song.audio}
        onTimeUpdate={updateProgress}
        onEnded={nextSong}
      />
    </div>
  );
}

export default AudioPlayerPage;
