import React, { useState, useContext } from 'react';
import { PlayerContext } from '../context/PlayerContext';

function MusicSearch() {
  const [query, setQuery] = useState('');
  const [songs, setSongs] = useState([]);
  const { playSong, currentSong, setPlaylist } = useContext(PlayerContext);

  const handleSearch = async () => {
    if (!query) return;
    const res = await fetch(`http://127.0.0.1:5000/api/music/search?query=${query}`);
    const data = await res.json();
    setSongs(data.results);
    setPlaylist(data.results); // set playlist for next/prev
  };

  return (
    <div>
      <div className="input-group mb-4">
        <input
          type="text"
          className="form-control"
          placeholder="Search music..."
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          onKeyDown={(e) => e.key === 'Enter' && handleSearch()}
        />
        <button className="btn btn-success" onClick={handleSearch}>Search</button>
      </div>

      <div className="row">
        {songs.map((song) => (
          <div key={song.id} className="col-6 col-md-4 col-lg-3 mb-3">
            <div
              className={`card text-dark ${currentSong?.id === song.id ? 'border-success' : ''}`}
              style={{cursor: 'pointer'}}
              onClick={() => playSong(song)}
            >
              <img src={`https://via.placeholder.com/150`} className="card-img-top" alt="Album Art" />
              <div className="card-body p-2">
                <h6 className="card-title">{song.name}</h6>
                <p className="card-text"><small>{song.artist_name}</small></p>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default MusicSearch;
