import React, { useRef, useState, useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom";

function MusicPlayer() {
  const { state } = useLocation();
  const navigate = useNavigate();
  const audioRef = useRef(null);

  const playlist = state?.songs || [];
  const [index, setIndex] = useState(state?.index || 0);
  const song = playlist[index];

  const [isPlaying, setIsPlaying] = useState(false);
  const [progress, setProgress] = useState(0);
  const [duration, setDuration] = useState(0);
  const [volume, setVolume] = useState(0.8);

  // ‚ùå NO AUTOPLAY (browser blocks it)
  useEffect(() => {
    if (!audioRef.current || !song) return;
    audioRef.current.load();
    audioRef.current.volume = volume;
    setIsPlaying(false);
    setProgress(0);
  }, [index]);

  if (!song) {
    return (
      <div className="text-center mt-5">
        <h4>No song selected</h4>
        <button className="btn btn-primary" onClick={() => navigate("/")}>
          Go Back
        </button>
      </div>
    );
  }

  // ‚úÖ USER CLICK PLAY (REQUIRED)
  const togglePlay = async () => {
    if (!audioRef.current) return;

    try {
      if (isPlaying) {
        audioRef.current.pause();
        setIsPlaying(false);
      } else {
        await audioRef.current.play(); // ‚úÖ allowed
        setIsPlaying(true);
      }
    } catch (err) {
      alert("Audio cannot be played. Check audio URL.");
      console.error(err);
    }
  };

  const next = () => {
    if (index < playlist.length - 1) {
      setIndex(index + 1);
    }
  };

  const prev = () => {
    if (index > 0) {
      setIndex(index - 1);
    }
  };

  const updateProgress = () => {
    if (!audioRef.current) return;
    const current = audioRef.current.currentTime;
    const total = audioRef.current.duration || 0;
    if (isFinite(total)) {
      setProgress((current / total) * 100);
    }
  };

  const seek = (e) => {
    if (!audioRef.current || !duration) return;
    audioRef.current.currentTime = (e.target.value / 100) * duration;
  };

  return (
    <div style={styles.wrapper}>
      <div style={styles.header}>
        <button onClick={() => navigate("/")} style={styles.backBtn}>
          ‚¨Ö Back
        </button>
        <span>Music Player</span>
      </div>

      <div style={styles.artBox}>
        <div style={styles.artInner}>üéµ</div>
      </div>

      <h3>{song.name}</h3>
      <p style={{ color: "#aaa" }}>{song.artist_name}</p>

      <input
        type="range"
        min="0"
        max="100"
        value={progress}
        onChange={seek}
        style={styles.progress}
      />

      <div style={styles.controls}>
        <button onClick={prev}>‚èÆ</button>
        <button onClick={togglePlay} style={styles.playBtn}>
          {isPlaying ? "‚è∏" : "‚ñ∂"}
        </button>
        <button onClick={next}>‚è≠</button>
      </div>

      <div style={styles.volume}>
        üîä
        <input
          type="range"
          min="0"
          max="1"
          step="0.01"
          value={volume}
          onChange={(e) => {
            setVolume(e.target.value);
            audioRef.current.volume = e.target.value;
          }}
        />
      </div>

      {/* ‚úÖ AUDIO ELEMENT */}
      <audio
        ref={audioRef}
        src={song.audio_url}
        onTimeUpdate={updateProgress}
        onLoadedMetadata={() => setDuration(audioRef.current.duration)}
        onEnded={next}
        controls={false}
      />
    </div>
  );
}

const styles = {
  wrapper: {
    height: "100vh",
    background: "radial-gradient(circle at top, #1b3a57, #000)",
    color: "#fff",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
    fontFamily: "Segoe UI"
  },
  header: {
    position: "absolute",
    top: 20,
    width: "90%",
    display: "flex",
    justifyContent: "space-between"
  },
  backBtn: {
    background: "none",
    color: "#fff",
    border: "none",
    cursor: "pointer"
  },
  artBox: {
    width: 220,
    height: 220,
    border: "3px solid #2b6cb0",
    boxShadow: "0 0 30px #2b6cb0",
    display: "flex",
    alignItems: "center",
    justifyContent: "center"
  },
  artInner: {
    fontSize: 80,
    opacity: 0.3
  },
  progress: {
    width: "60%",
    marginTop: 20
  },
  controls: {
    marginTop: 20,
    display: "flex",
    gap: 20,
    fontSize: 24
  },
  playBtn: {
    fontSize: 30,
    background: "#2b6cb0",
    border: "none",
    borderRadius: "50%",
    width: 60,
    height: 60,
    color: "#fff"
  },
  volume: {
    marginTop: 20,
    display: "flex",
    gap: 10,
    alignItems: "center"
  }
};

export default MusicPlayer;
