import React, { useRef, useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

function PlayerPage() {
  const location = useLocation();
  const navigate = useNavigate();
  const song = location.state?.song; // get song from state
  const playNext = location.state?.playNext;
  const playPrev = location.state?.playPrev;

  const audioRef = useRef(null);
  const [isPlaying, setIsPlaying] = useState(true);
  const [progress, setProgress] = useState(0);
  const [volume, setVolume] = useState(1);

  useEffect(() => {
    if (audioRef.current && song) {
      audioRef.current.play();
      setIsPlaying(true);
    }
  }, [song]);

  if (!song) {
    return (
      <div className="container mt-5">
        <h3>No song selected</h3>
        <button className="btn btn-primary" onClick={() => navigate(-1)}>Go Back</button>
      </div>
    );
  }

  const togglePlay = () => {
    if (!audioRef.current) return;
    if (isPlaying) audioRef.current.pause();
    else audioRef.current.play();
    setIsPlaying(!isPlaying);
  };

  const handleTimeUpdate = () => {
    if (!audioRef.current) return;
    setProgress((audioRef.current.currentTime / audioRef.current.duration) * 100);
  };

  const handleProgressChange = (e) => {
    if (!audioRef.current) return;
    audioRef.current.currentTime = (e.target.value / 100) * audioRef.current.duration;
    setProgress(e.target.value);
  };

  const handleVolumeChange = (e) => {
    const vol = e.target.value;
    if (audioRef.current) audioRef.current.volume = vol;
    setVolume(vol);
  };

  return (
    <div className="container mt-5">
      <h2>Now Playing</h2>
      <div className="card p-3 mt-3">
        <h4>{song.name}</h4>
        <p><small>{song.artist_name}</small></p>

        <div className="d-flex align-items-center gap-2 mb-2">
          <button className="btn btn-secondary" onClick={playPrev}>⏮️ Prev</button>
          <button className="btn btn-primary" onClick={togglePlay}>{isPlaying ? '⏸️ Pause' : '▶️ Play'}</button>
          <button className="btn btn-secondary" onClick={playNext}>⏭️ Next</button>
        </div>

        <input
          type="range"
          min="0"
          max="100"
          value={progress}
          onChange={handleProgressChange}
          className="form-range mb-2"
        />

        <input
          type="range"
          min="0"
          max="1"
          step="0.01"
          value={volume}
          onChange={handleVolumeChange}
          className="form-range"
          style={{ width: '200px' }}
        />

        <audio
          ref={audioRef}
          src={song.audio}
          onTimeUpdate={handleTimeUpdate}
          onEnded={playNext}
        />
      </div>
    </div>
  );
}

export default PlayerPage;
